import { createSlice } from '@reduxjs/toolkit'


export const transactionSlice = createSlice({
  name: 'transaction',
  initialState:{
    transactions: []
  },
  reducers: {
    deleteTransaction: (state,action) => {
      // Redux Toolkit allows us to write "mutating" logic in reducers. It
      // doesn't actually mutate the state because it uses the Immer library,
      // which detects changes to a "draft state" and produces a brand new
      // immutable state based off those changes
      
      return {
        
        transactions:state.transactions.filter(transactions=>transactions.id !== action.payload)
    }
    },
    AddNewTransaction: (state,action) => {
    
       
            state.transactions = [action.payload,...state.transactions]
        
    },
    resetTransactions: (state,action) => {
        return{
            ...state,
            transactions:action.payload
        }
    },
    
  },
})

// Action creators are generated for each case reducer function
export const { deleteTransaction, AddNewTransaction, resetTransactions } = transactionSlice.actions

export default transactionSlice.reducer
